import java.util.Scanner;

public class Average {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		Scanner sc=new Scanner(System.in);
		int a=sc.nextInt();
		int b=sc.nextInt();
		int c=sc.nextInt();
		String name=sc.next();
		int ans=(a+b+c)/3;
		System.out.println(name+"gets "+ans+" marks out of 100");
		
		

	}

}
